tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'Upload an image',file:url+'/dialog-v4.htm',width:350,height:135,buttons:[{text:'Close',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Upload an image',icon:'image',text:'Upload',onclick:jbBox});editor.addMenuItem('jbimages',{text:'Upload image',icon:'image',context:'insert',onclick:jbBox})});